SELECT di.TING_CCOD, di.DING_NDOCTO, di.ingr_ncorr, rep.* FROM dbo.AAAA_BORRAR_ENERO_2016_reporte_dicom rep
      INNER JOIN DETALLE_INGRESOS di
            ON rep.letra=di.DING_NDOCTO
                  AND rep.monto=di.DING_MDOCTO
      INNER JOIN estados_detalle_ingresos edi
            ON di.EDIN_CCOD=edi.EDIN_CCOD
                  AND rep.estado COLLATE Modern_Spanish_CI_AS = edi.EDIN_TDESC COLLATE Modern_Spanish_CI_AS

