--Alumnos Nuevos CAE (se deben marcar sus descuentos)
select distinct pers_nrut,b.pers_ncorr,b.post_ncorr,c.ofer_ncorr 
from         personas as a inner join
             postulantes as b 
                on a.pers_ncorr = b.pers_ncorr
             join alumnos c
                on b.post_ncorr=c.post_ncorr
where     (b.peri_ccod = 222) and (b.pers_ncorr in
                          (select     pers_ncorr
                            from          personas
                            where      (pers_nrut in (13338998, 13832613, 14159764, 14184198, 14220376, 15088289, 15840561, 16044941, 16347451, 16363130, 
                                                   16365760, 16366592, 16369558, 16397512, 16428441, 16509747, 16548100, 16572778, 16576551, 16576807, 16607064, 16661258, 
                                                   16710354, 16713485, 16728048, 16759477, 16843107, 16856273, 16904502, 16907221, 16933760, 16940986, 16945269, 16945600, 
                                                   16948868, 16969565, 16985532, 16998424, 17008925, 17010144, 17021851, 17029777, 17070019, 17079173, 17086901, 17111135, 
                                                   17136340, 17199510, 17234467, 17234952, 17265022, 17302614, 17308282, 17340825, 17342406, 17349201, 17381680, 17391111, 
                                                   17397551, 17397927, 17404269, 17407580, 17422125, 17448896, 17486872, 17507326, 17577746, 17603849, 17622338, 17623754, 
                                                   17651014, 17672728, 17680845, 17681046, 17810237, 17812648, 17817920, 17890688, 17961183, 17961945, 17968212, 17985975, 
                                                   17986588, 17986757, 17991040, 18015528, 18096957, 18212640, 18213264, 19437454))))
and b.epos_ccod=2
and alum_nmatricula not in (7777)
and b.post_ncorr not  in (161690)
